import React, { Component } from 'react'

class Parent extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             name:'Akash'   
        }
    }
    componentDidMount(){
        setInterval(()=>{
            this.setState({
                name:'akash'
            })
        },2000)
    }
    render() {
        return (
            <div>
               Parent Component 
            </div>
        )
    }
}

export default Parent
