import React, { Component } from 'react'

class Form extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             name:''
        }
    }
    handleChange = (e) =>{
        this.setState({
            name
        })
    }
    render() {
        return (
            <div>
                From Component<br></br>
            <label>Name: </label>
            <input type='text' value={this.state.name} onChange={this.handleChange}/>
            </div>
        )
    }
}

export default Form
